(1, 'KH01', 'HTML', '<p>Ngôn ngữ định dạng tạo kiểu trang web1</p>', '<!DOCTYPE html>\n<html>\n<title>HTML Tutorial</title>\n<body>\n\n<h1>This is a heading</h1>\n<p>This is a paragraph1.</p>\n\n</body>\n</html>', 'kh_hinhanh-1668342907643.png', 'html', 1, 1, '2022-11-12 04:55:18'),
(2, 'KH02', 'CSS', '<p>Ngôn ngữ định dạng tạo kiểu trang web</p>', '<!DOCTYPE html>\n<html>\n<head>\n<style>\nbody {\n  background-color: lightblue;\n}\n\nh1 {\n  color: white;\n  text-align: center;\n}\n\np {\n  font-family: verdana;\n  font-size: 20px;\n}\n</style>\n</head>\n<body>\n\n<h1>My First CSS Example</h1>\n<p>This is a paragraph.</p>\n\n</body>\n</html>', 'kh_hinhanh-1668265177115.png', 'css', 1, 1, '2022-11-12 04:55:18'),
(3, 'KH03', 'JavaScript', '<p>Khóa học dành cho các bạn chưa biết gì về javascript</p>', '<!DOCTYPE html>\n<html>\n<body>\n\n<p id=\"demo\">Click the button to change the layout of this paragraph</p>\n\n<button onclick=\"myFunction()\">Click Me!</button>\n\n<script>\nfunction myFunction() {\n  let x = document.getElementById(\"demo\");\n  x.style.fontSize = \"25px\"; \n  x.style.color = \"red\"; \n}\n</script>\n\n</body>\n</html>\n', 'kh_hinhanh-1668265177115.png', 'javascript', 1, 1, '2022-11-12 04:55:18'),
(5, 'KH05', 'MYSQL', '<p>Ngôn ngữ truy cập cơ sở dữ liệu</p>', 'SELECT * FROMCustomer\nWHERE Country= \'Mexico\';', 'kh_hinhanh-1668265177115.png', 'mysql', 0, 1, '2022-11-12 04:55:18'),
(6, 'KH06', 'Python', '<p>Một ngôn ngữ lập trình phổ biến</p>', 'if 5 > 2:\n  print(\"Five is greater than two!\")', 'kh_hinhanh-1668265177115.png', 'python', 1, 1, '2022-11-12 04:55:18');

