DELIMITER $$
CREATE TRIGGER `create_makh` BEFORE INSERT ON `khoa_hoc` FOR EACH ROW BEGIN
DECLARE id varchar(100);

SET id = (SELECT CONCAT("KH0",SUBSTRING(kh_makh,4)+1) FROM khoa_hoc ORDER BY SUBSTRING(kh_makh,4)*1 DESC LIMIT 1);

IF id IS NULL 
THEN SET NEW.kh_makh = 'KH01';
ELSE SET NEW.kh_makh = id;
END IF;
END
$$
DELIMITER ;

